import React, { Component } from 'react';
import LoginView from './login';
import RegisterView from './register';
import ListView from './list';
import { LOGIN_PAGE_ID,REGISTER_PAGE_ID,LIST_PAGE_ID } from './constants';
export default class AppNavigation extends Component{
  constructor(props){
    super(props);
    let page = localStorage.getItem("Tree App");
    if(page === "" || page === null || page === undefined){
      page = LOGIN_PAGE_ID
    }
    this.state={
      page: page,

    }
    this.onPageChange=this.onPageChange.bind(this);
  }
  onPageChange(page,id){
    this.setState({
      page:page,
      id:id,
    })
    localStorage.setItem("Tree App",page);
    localStorage.setItem("Tree_Obj_id",id)
  }
  render(){
    const { page,id } = this.state;
    return(
      <div>
          {page === LOGIN_PAGE_ID &&
              <LoginView onPageChange={this.onPageChange} page={page} />
          }
          {page === REGISTER_PAGE_ID &&
              <RegisterView onPageChange={this.onPageChange} page={page} />
          }
          {page === LIST_PAGE_ID &&
              <ListView onPageChange={this.onPageChange} />
          }
          {page === EDIT_PAGE_ID &&
              <EditView onPageChange={this.onPageChange} id={id}/>
          }
          {page === VIEW_PAGE_ID &&
              <EditView onPageChange={this.onPageChange} id={id}/>
          }
      </div>
    );
  }
}
