import React, { Component } from 'react';
import { REGISTER_PAGE_ID,LIST_PAGE_ID } from './constants';
import './tree.css';
export default class DetailView extends Component{
  constructor(props){
    super(props);
    let id = props.id;
    let treeArr = JSON.parse(localStorage.getItem("Tree_Arr"));
    let treeObj;
    for (treeObj of treeArr){
      if(treeObj.id === id){
        break;
      }
    }
    this.state = {
      treeArr : treeArr,
      id : id,
      name : treeObj.name,
      scientificName : treeObj.scientificName,
      treeImageUrl : treeObj.treeImageUrl,
      seedImageUrl : treeObj.seedImageUrl,
      shortDescription : treeObj.shortDescription,
    }
    this.onBackClick= this.onBackClick.bind(this);

}
onBackClick(event){
  event.preventDefault();
  this.props.onPageChange(LIST_PAGE_ID)
}

render(){
    return(
      <div>
        <center>
          <span className="login"><h1 style={{color:"darkblue"}}>{this.state.name}</h1></span>
          <br /><br />
          <br />

          <p><span style={{color:"blue"}}>Scientific name:</span> <b style={{color:"red"}}>{this.state.scientificName}</b></p>
          <center>
              <br />
              <img src={this.state.treeImageUrl} alt={this.state.name} height="40%" width="40%" />
              <figcaption>{this.state.name}{"(Tree Image)"}</figcaption>
              <br />
              <br />
              <br />
              <img src={this.state.seedImageUrl} alt={this.state.name} height="40%" width="40%" />
              <figcaption>{this.state.name}{"(Seed Image)"}</figcaption>
              <br />
          </center>
          <p style={{margin:"1%"}}><b>Description:</b> <br /><br /><p style={{margin:"2% 3% 2% 5%"}} dangerouslySetInnerHTML={{__html: this.state.shortDescription}} /></p>


          <button style={{width:"10%",marginBottom:"20%"}} onClick={(event)=>this.onBackClick(event)}>Back</button>
          </center>
      </div>
    );
  }
}
