import React, { Component } from 'react';
import { LIST_PAGE_ID,TREE_ARR } from './constants';

export default class EditView extends Component{
  constructor(props){
    super(props);
    let treeObj;
    for (treeObj of TREE_ARR){
      if(treeObj.id === props.id){
        localStorage.setItem("Tree_Object",JSON.stringify(treeObj));
        return;
      }
    }
}
onBackClick(event){
  event.preventDefault();
  this.props.onPageChange(LIST_PAGE_ID)
}
render(){



    return(
      <div>
        <h1>Edit</h1>
        <EditTree />
        <button onClick={(event)=>this.onBackClick(event)}>Back</button>
      </div>
    );
  }
}

class EditTree extends Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <div>
          <input
              type="text"
              value=""
              onChange = {(event)=>this.onNameChange(event)}
          />
          <br />
          <br />
          <input
              type="text"
              value=""
              onChange = {(event)=>this.onScientificNameChange(event)}
          />
          <br />
          <br />
          <input
              type="text"
              value=""
              onChange = {(event)=>this.onTreeImageURLChange(event)}
          />
          <br />
          <br />
          <input
              type="text"
              value=""
              onChange = {(event)=>this.onSeedImageURLChange(event)}
          />
          <br />
          <br />
          <textarea
              type="text"
              value=""
              onChange = {(event)=>this.onDescriptionChange(event)}
          />
          <br />
          <br />
      </div>
    );
  }
}
