import React, { Component } from 'react';
import { LOGIN_PAGE_ID,TREE_ARR,EDIT_PAGE_ID,VIEW_PAGE_ID } from './constants';


export default class ListView extends Component{
  constructor(props){
    super(props);
    this.state={
      treeArr : JSON.parse(localStorage.getItem("Tree_Arr")),
      searchTerm:"",
      loggedInUser:JSON.parse(localStorage.getItem("Logged-in-User")),
    }
    this.onViewClick=this.onViewClick.bind(this);
    this.onEditClick=this.onEditClick.bind(this);
    this.onSearchChange=this.onSearchChange.bind(this);
    this.onLogoutClick=this.onLogoutClick.bind(this);
  }
onViewClick(event,id){
  event.preventDefault();
  this.props.onPageChange(VIEW_PAGE_ID,id);
}
onEditClick(event,id){
  event.preventDefault();
  this.props.onPageChange(EDIT_PAGE_ID,id)
}
onSearchChange(event){
  this.setState({
    searchTerm:event.target.value,
  })

}
onLogoutClick(event){
  localStorage.removeItem("Logged-in-User");
  this.setState({
    loggedInUser:"",
  })
  this.props.onPageChange(LOGIN_PAGE_ID);
}

  render(){
    const filteredTreesArr = this.state.treeArr.filter(
      (treeObj)=>
            treeObj.name.toLowerCase().includes( this.state.searchTerm.toLowerCase() )
    )
      return(
        <div>



             <p className="welcome">Welcome <b><em>{this.state.loggedInUser.firstName}</em></b>,</p>
             <div>
                 <h1>Tree List</h1>
                 <center>
                     <input
                        type="text"
                        value={this.state.searchTerm}
                        placeholder="Search your Tree Here"
                        onChange={(event)=>this.onSearchChange(event)}
                    />
                </center>
                <br />

                <TreeList treeArr={filteredTreesArr} onViewClick={this.onViewClick} onEditClick={this.onEditClick}/>
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <button style={{width:"10%",marginTop:"1%",marginBottom:"20%"}}onClick={(event)=>this.onLogoutClick(event)}>Logout</button>
            </div>

      </div>
      );
  }
}

function TreeList(props){
  let treeElements = props.treeArr.map(
    (treeObj) =>
    <tr className="table-content">
      <td>{treeObj.name}</td>
      <td>
        <button style={{width:"30%"}} onClick={(event,id)=>{props.onViewClick(event,treeObj.id)}}>View</button>
        <button style={{width:"30%"}} onClick={(event,id)=>{props.onEditClick(event,treeObj.id)}}>Edit</button>
      </td>
    </tr>
  )
  return(
    <table>
      <tr className="table-header">
        <th>Tree Name</th>
        <th>For more information</th>
      </tr>
      {treeElements}
    </table>
  );
}
