import React, { Component } from 'react';
import { REGISTER_PAGE_ID,LIST_PAGE_ID } from './constants';
let treeArr = [
  {
    id: 1,
    name: "White Oaks Tree",
    scientificName : "Quercus alba",
    treeImageUrl : "https://upload.wikimedia.org/wikipedia/commons/thumb/e/e6/Keeler_Oak_Tree_-_distance_photo%2C_May_2013.jpg/1024px-Keeler_Oak_Tree_-_distance_photo%2C_May_2013.jpg",
    seedImageUrl : "https://i.pinimg.com/originals/ea/36/e0/ea36e0e80ec95b3fbbbfa573c3fa37dd.jpg",
    shortDescription : "Quercus alba, the white oak, is one of the preeminent hardwoods of eastern and central North."
  },{
    id: 2,
    name: "Red Oak Tree",
    scientificName : "Quercus rubra",
    treeImageUrl : "https://cdn.thetreecenter.com/c/uploads/red-oak-1.jpg",
    seedImageUrl : "https://www.explorejeffersonpa.com/wp-content/uploads/2016/09/g09414art05a.jpg",
    shortDescription : "The red oak is usually 20 to 30 metres tall, with a thick trunk sometimes more than 120 centimetres in diameter."
  },{
    id: 3,
    name: "Jack Fruit Tree",
    scientificName : "Artocarpus heterophyllus",
    treeImageUrl : "https://cdn.shopify.com/s/files/1/0059/8835/2052/products/Jackfruit-450w_grande.jpg?v=1549646944",
    seedImageUrl : "https://images-na.ssl-images-amazon.com/images/I/41bpy58M7zL._SX355_.jpg",
    shortDescription : "The jackfruit, also known as jack tree, is a species of tree in the fig, mulberry, and breadfruit family."
  },
]
export default class ListView extends Component{
  constructor(props){
    super(props);
    this.state={
      treeArr : treeArr,
    }
    this.onViewClick=this.onViewClick.bind(this);
    this.onEditClick=this.onEditClick.bind(this);
  }
onViewClick(event,id){
  alert(id);
}
onEditClick(event,id){
  alert(id);
}

  render(){
      return(
        <div>
         <h1>Tree List</h1>
         <TreeList treeArr={this.state.treeArr} />
        </div>
      );
  }
}

function TreeList(props){
  let treeElements = props.treeArr.map(
    (treeObj) =>
    <tr>
      <td>{treeObj.name}</td>
      <td>
        <button onClick={(event,id)=>this.onViewClick(event,treeObj.id)}>View</button>{" "}
        <button onClick={(event,id)=>this.onEditClick(event,treeObj.id)}>Edit</button>
        </td>
    </tr>
  )
  return(
    <table>
      <tr>
        <th>Tree Name</th>
        <th>For more information</th>
      </tr>
      {treeElements}
    </table>
  );
}
