import React, { Component } from 'react';
import { REGISTER_PAGE_ID,LIST_PAGE_ID,TREE_ARR } from './constants';
export default class EditView extends Component{
  constructor(props){
    super(props);
}
onBackClick(event){
  event.preventDefault();
  this.props.onPageChange(LIST_PAGE_ID)
}
render(){

    return(
      <div>
        <h1>Edit</h1>
        <EditTree onPageChange={this.props.onPageChange}/>
        <button onClick={(event)=>this.onBackClick(event)}>Back</button>
      </div>
    );
  }
}

export class EditTree extends Component{
  constructor(props){
    let treeObj;
    let id = localStorage.getItem("Tree_Obj_id")
   for (treeObj of TREE_ARR){
     if(treeObj.id === props.id){
       alert(treeObj.id)
       return treeObj;
     }
     break;
   }
    super(props);
    this.state={
      name:treeObj.name,
      scientificName:treeObj.scientificName,
      treeImageUrl:treeObj.treeImageUrl,
      seedImageUrl:treeObj.seedImageUrl,
      shortDescription:treeObj.shortDescription,

    }
    this.onTreeNameChange = this.onTreeNameChange.bind(this);
    this.onScientificName = this.onScientificName.bind(this);
    this.onTreeImageURLChange = this.onTreeImageURLChange.bind(this);
    this.onSeedImageURLChange = this.onSeedImageURLChange.bind(this);
    this.onTreeDescriptionChange = this.onTreeDescriptionChange.bind(this);
    this.onUpdateClick = this.onUpdateClick.bind(this);

  }
  onTreeNameChange(event){
    this.setState({
      name:event.target.value,
    })
  }
  onScientificName(event){
    this.setState({
      scientificName:event.target.value,
    })
  }
  onTreeImageURLChange(event){
    this.setState({
      treeImageUrl:event.target.value,
    })
  }
  onSeedImageURLChange(event){
    this.setState({
      seedImageUrl:event.target.value,
    })
  }
  onTreeDescriptionChange(event){
    this.setState({
      shortDescription:event.target.value,
    })
  }
  onUpdateClick(event){
    const { name,scientificName,treeImageUrl,seedImageUrl,shortDescription } = this.state;
    event.preventDefault();
    let treeObj;
    for (treeObj of TREE_ARR){
      if(treeObj.id === this.props.id){
        treeObj.name = name;
        treeObj.scientificName = scientificName;
        treeObj.treeImageUrl = treeImageUrl;
        treeObj.seedImageUrl = seedImageUrl;
        treeObj.shortDescription = shortDescription;
      }
      break;
    }
    this.props.onPageChange(LIST_PAGE_ID);
  }
render(){
return(
  <div>
  <input
    type="text"
    value={this.state.name}
    onChange={(event)=>this.onTreeNameChange(event)}
  />
  <br />
  <br />
  <input
    type="text"
    value={this.state.scientificName}
    onChange={(event)=>this.onScientificName(event)}
  />
  <br />
  <br />
  <input
    type="text"
    value={this.state.treeImageUrl}
    onChange={(event)=>this.onTreeImageURLChange(event)}
  />
  <br />
  <br />
  <input
    type="text"
    value={this.state.seedImageUrl}
    onChange={(event)=>this.onSeedImageURLChange(event)}
  />
  <br />
  <br />
  <textarea
    type="text"
    value={this.state.shortDescription}
    onChange={(event)=>this.onTreeDescriptionChange(event)}
  />
  <br />
  <br />
  <button onClick={(event)=>{this.onUpdateClick(event)}}>Update</button>
  </div>
);
}
}
