import React, { Component } from 'react';
import { LOGIN_PAGE_ID } from './constants';
export default class LoginView extends Component{
  constructor(props){
    super(props);
    this.state={
      firstName:"",
      lastName:"",
      email:"",
      username:"",
      password:"",
      message : "",

    }
    this.onUsernameChange = this.onUsernameChange.bind(this);
    this.onPasswordChange = this.onPasswordChange.bind(this);
    this.onFirstNameChange = this.onFirstNameChange.bind(this);
    this.onLastNameChange = this.onLastNameChange.bind(this);
    this.onEmailChange = this.onEmailChange.bind(this);
  }
  onFirstNameChange(event){
    this.setState({
      firstName:event.target.value,
    })
  }
  onLastNameChange(event){
    this.setState({
      lastName:event.target.value,
    })
  }
  onEmailChange(event){
    this.setState({
      email:event.target.value,
    })
  }
  onUsernameChange(event){
    this.setState({
      username:event.target.value,
    })
  }
  onPasswordChange(event){
    this.setState({
      password:event.target.value,
    })
  }
  onSubmitClick(event){
    const { username,password,firstName,lastName,email } = this.state;
    event.preventDefault();
    if(username ==="" || username ===null || username === undefined){
      this.setState({
        message : "This field is required"
      })
    }
    if(password ==="" || password ===null || password === undefined){
      this.setState({
        message : "This field is required"
      })
    }
    this.props.onPageChange(LOGIN_PAGE_ID);
  }

  render(){
    const { username,password,firstName,lastName,email } = this.state;
    return(
      <div>
          <fieldset>
              <legend>Register</legend>
              <input
                type="text"
                placeholder="First Name"
                value={firstName}
                onChange={ (event)=>{this.onFirstNameChange(event)}}
              />
              <br />
              <br />
              <input
                type="text"
                placeholder="Last Name"
                value={lastName}
                onChange={ (event)=>{this.onLastNameChange(event)}}
              />
              <br />
              <br />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={ (event)=>{this.onEmailChange(event)}}
              />
              <br />
              <br />
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={ (event)=>{this.onUsernameChange(event)}}
              />
              <br />
              <br />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={ (event)=>{this.onPasswordChange(event)}}
              />
              <br />
              <br />
              <button onClick={(event)=>{this.onSubmitClick(event)}}>Register</button>
              <br />
              <br />
            </fieldset>
      </div>
    );
  }
}
